package com.televisa.commons.services.utilities;import java.io.IOException;import org.apache.commons.codec.binary.Base64;public class Base64Encoding {	public static String encoding(String key){		if(key != null && key.length() > 0){			byte[] encoded = Base64.encodeBase64(key.getBytes());     			return new String(encoded);			}else{			return null;		}	}		public static String decoding(String key){		if(key != null && key.length() > 0){			byte[] decoded = Base64.decodeBase64(key.getBytes());			return new String(decoded);			}else{			return null;		}	}	    public static void main(String args[]) throws IOException {        String original = "original String before base64 encoding in Java";        System.out.println("Original String: " + original );        String encoded = encoding(original);        System.out.println("Base64 Encoded String : " + encoded);        String decoded = decoding(encoded);        System.out.println("Base 64 Decoded  String : " + decoded);    }}